                "name": toSave[1].name,
                "notes": toSave[1].notes
            },
            {
                "name": toSave[2].name,
                "notes": toSave[3].notes
            },
            {
                "name": toSave[3].name,
                "notes": toSave[3].notes
            }

        ]  }

        fetch('http://localhost:4000/beats', {
            method: 'POST', 
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(newBeat),
          })
          .then(res => res.json())
          .then(data => {
            console.log(data);
          })
          .catch((error) => {
            console.error('Error:', error);
          });
    }
// use an if statement to say that if selected > 0 onNotes is the array in the id'd array
       const gettin = fetched[selected-1]


    let newKickNotes = gettin
    let content = trackList.map((track, trackID) => {
        let key = trackID
        let { title, onNotes, soundFile, id } = track

        let soundFilePath = soundFiles[soundFile]
      let runningNotes = track.onNotes
      let loadedNotes = gettin.trackListInfo
      let loadKicks = gettin.trackListInfo[0].notes
      let loadSnares = gettin.trackListInfo[1].notes
      let loadHho = gettin.trackListInfo[2].notes
      let loadHhc = gettin.trackListInfo[3].notes

      console.log('runningNotes', track.title)
      console.log('kick', track[0])
      console.log('id', trackID)
       if (runningNotes != loadedNotes){ loadedNotes = runningNotes }

        return (
         <> 

            <Track

                id = {id}
                key={title}
                trackID={+trackID}
                currentStepID={currentStepID}
                title={title}
                noteCount={noteCount}
                onNotes={onNotes}
                soundFilePath={soundFilePath}
            />
            </>
        )
    })

    // {function swapBeat (e) {

    //     let kickNotes = content[0].props.children.props.onNotes
    //     const newKickNotes = gettin.trackListInfo[0].notes
    //     kickNotes = newKickNotes
    //         console.log('kicknotes', kickNotes)}
    //     }

    return (
        <>


        <div className="track-list">

            {content}
        </div>
        <button onClick={(e) => saveIt()} > SAVE BEAT </ button>
        {/* <button onClick={(e) => swapBeat()} >Load Beat </button> */}

        </>
    )
}

export default memo(TrackList)
